
public class main {
// res++ Ч прибавление единицы
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float num_1=23.67f, num_2=43.4f, res;
		res = num_1+num_2; //res+=45f
		System.out.println(res);
		res = num_1-num_2;
		System.out.println(res);
		res = num_1*num_2;
		System.out.println(res);
		res = num_1/num_2;
		System.out.println(res);
		res = num_1%num_2;
		System.out.println(res);
	}

}
