
public class main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=234;
		// ==, !=, >, <, >=, <=  
		if (i!=234) {
			System.out.println("i is not 234");
		}
		else if(i >100){
			System.out.println("i is bigger than 100");
		} 
		else {
			System.out.println("i is unknown");
		}
		
		// объединение усл.операторов 
		// || - или, && - и
		
	}

}
